import { useState, useEffect } from "react";
import {
    WcProceedingWrapper,
    WcProceedingTitle,
    WcProceedingMain,
    WcParticipantiv,
    WcParticipantivBoard
} from "./wc_proceeding.styles";

import { MouseEvent } from "react";

const items = [
    {
        name: "고릴라",
        src: "http://c.files.bbci.co.uk/509D/production/_112873602_whatsappimage2020-06-12at11.23.53-2.jpg"
    },
    {
        name: "호랑이",
        src: "https://ww.namu.la/s/a6a99cb9d3488f39fbfe64f830faf674d00efde4c8d9abc68909d0f5c2adb581959f2cb90f72a3d32e69a8dabafb6238cb70a58e5cafa9a705c2438000247d12668ae536a1e1ea1be062883f5a0d2b32"
    },
    {
        name: "북극곰",
        src: "data:image/jpeg;base64,"
    },
    {
        name: "판다",
        src: "https://w.namu.la/s/be268d9db8a332022cc48c082a1bfa1009194ad7e37d8f92ca43c0ea12ea30eca34b750204f2bd02c49331e6c9ceb340408816c8255c17e582bc503955442f512168357a1cb0b94663797246a721ff84daf6186f0dd255430b78845f81dc21bd"
    },
    {
        name: "기린",
        src: "data:image/jpeg;base64,"
    },
    {
        name: "레서판다",
        src: "https://img.animalplanet.co.kr/news/2020/02/22/700/61o6ms9k485kgf4xs168.jpg"
    },
    {
        name: "코끼리",
        src: "data:image/jpeg;base64,"
    },
    {
        name: "사자",
        src: "https://ww.namu.la/s/e37127a08fffcc08037fff2c79586f146930f780c9ae455b3659f8dc2244754da36d6433ee49bf457a4a090220be2f20d928090428cc62103334aae5b8c0dcb8cf89271e1baa303d0bfe6414cdcc343c"
    }
];

const WcProceedingPresenter = () => {
    const [foods, setFoods] = useState<any[]>([]);
    const [displays, setDisplays] = useState<any[]>([]);
    const [winners, setWinners] = useState<any[]>([]);
    const [worldCupstate, setWorldCupstate] = useState(items.length);
    useEffect(() => {
        items.sort(() => Math.random() - 0.5);
        setFoods(items);
        setDisplays([items[0], items[1]]);
    }, []);

    const clickHandler = (animal: any) => (event: MouseEvent<HTMLDivElement>) => {
        event.currentTarget.classList.add("click__active")
        setTimeout(() => {
            if (foods.length <= 2) {
                if (winners.length === 0) {
                    setDisplays([animal]);
                } else {
                    let updatedFood = [...winners, animal];
                    setFoods(updatedFood);
                    setWorldCupstate(updatedFood.length)
                    setDisplays([updatedFood[0], updatedFood[1]]);
                    setWinners([]);
                }
            } else if (foods.length > 2) {
                setWinners([...winners, animal]);
                setDisplays([foods[2], foods[3]]);
                setFoods(foods.slice(2));
            }
            document.querySelector('.click__active')?.classList.remove("click__active")
        }, 1000)
    };

    return (
        <WcProceedingWrapper>
            <WcProceedingTitle>이상형 월드컵</WcProceedingTitle>
            <WcProceedingMain>
                <h2>
                    {
                        worldCupstate === 2 ? "결승" :
                            worldCupstate > 2 && worldCupstate + "강"
                    }
                </h2>
                {
                    displays.length === 1 && <div>우승을 축하합니다!</div>
                }
                <WcParticipantivBoard>
                    {displays && displays.map((el, index) => {
                        return (
                            <WcParticipantiv className="aaa" key={index + el.name} onClick={clickHandler(el)}>
                                <h3 className="name">{el?.name}</h3>
                                <img className="animal-img" src={el?.src} />
                            </WcParticipantiv>
                        );
                    })}
                </WcParticipantivBoard>
            </WcProceedingMain>
        </WcProceedingWrapper>
    );
};

export default WcProceedingPresenter;